Get-WmiObject -Class win32_userprofile -Filter Special='False' -ComputerName GBCI02PSH01 -Credential (Get-Credential -Credential $($env:USERNAME+'2')) | Select-Object -Property @{LABEL='ComputerName';EXPRESSION={$_.__SERVER}}, @{LABEL='UserName';EXPRESSION={$_.LocalPath.Replace('C:\Users\','')}}, @{LABEL='LastLogon';EXPRESSION={$_.ConvertToDateTime($_.lastusetime)}} | Sort-Object -Descending -Property ComputerName, LastLogon, UserName